import { createContext, useState, useEffect } from "react"
import { jwtDecode } from "jwt-decode";
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { api_base } from '../constants'; 

const AuthContext = createContext()

export default AuthContext

export const AuthProvider = ({ children }) => {
    let [user, setUser] = useState(localStorage.getItem("authTokens") ? jwtDecode(localStorage.getItem("authTokens")) : null)
    let [authTokens, setAuthTokens] = useState(localStorage.getItem("authTokens") ? JSON.parse(localStorage.getItem("authTokens")) : null)

    let navigate = useNavigate()

    let loginUser = async (username, password) => {
        try {
            let response = await axios.post(`${api_base}token/`, { username, password });
            setAuthTokens(response.data);
            setUser(jwtDecode(response.data.access));
            localStorage.setItem("authTokens", JSON.stringify(response.data));
            navigate("/my-quotes");
        } catch (error) {
            console.error("Login failed:", error); // Log the error
            navigate("/login"); // Navigate back to login on failure
            throw(error)  // handled by frontend to clear form
        }
    }

    let logoutUser = () => {
        setAuthTokens(null)
        setUser(null)
        localStorage.removeItem("authTokens")
        navigate("/login")
    }

    let updateToken = async () => {
        console.log("updating token")
        try {
            const response = await axios.post(`${api_base}token/refresh`, { refresh: authTokens?.refresh }); 
            setAuthTokens(response.data)
            setUser(jwtDecode(response.data.access))
            localStorage.setItem("authTokens", JSON.stringify(response.data))
        } catch {
            logoutUser()
        }
    }

    useEffect(() => {
        let interval =  setInterval(()=> {
            if (authTokens) {
                updateToken()
            }
        }, 3000)
        return ()=> clearInterval(interval)
    }, [authTokens])

    let contextData = {
        user: user,
        authTokens: authTokens,
        loginUser: loginUser,
        logoutUser: logoutUser
    }

    return (
        <AuthContext.Provider value={contextData}>
            { children }
        </AuthContext.Provider>
    )

}
